data.query:
query GetAllRuns($entity: String!, $project: String!) {
    project(name: $project, entityName: $entity) {
        runs(first: 10) {
            edges {
                node {
                    id
                    name
                    displayName
                    state
                    createdAt
                    summaryMetrics
                }
            }
            pageInfo {
                endCursor
                hasNextPage
            }
        }
    }
}

