data.query:
query GetRunHistory($entity: String!, $project: String!, $runName: String!, $specs: [JSONString!]!) {
    project(name: $project, entityName: $entity) {
        run(name: $runName) {
            id
            name
            displayName
            sampledHistory(specs: $specs)
        }
    }
}

