import json
from datetime import datetime

# Sample data from WooCommerce
woocommerce_products = [
    {
        "id": 21,
        "name": "LG OLED 77-inch C4",
        "sku": "LG-OLED-C4-77",
        "stock_quantity": 8,
        "meta_data": [
            {"key": "stock_threshold", "value": "4"},
            {"key": "supplier", "value": {
                "name": "LG Electronics",
                "contact": "b2b@lg.com",
                "supplier_id": "SUP012"
            }}
        ]
    },
    {
        "id": 18,
        "name": "Nintendo Switch OLED",
        "sku": "NINTENDO-SWITCH-OLED",
        "stock_quantity": 2,
        "meta_data": [
            {"key": "stock_threshold", "value": "12"},
            {"key": "supplier", "value": {
                "name": "Nintendo Distribution",
                "contact": "orders@nintendo-dist.com",
                "supplier_id": "SUP009"
            }}
        ]
    },
    {
        "id": 17,
        "name": "MacBook Pro 14-inch M3",
        "sku": "MACBOOK-PRO-14-M3",
        "stock_quantity": 3,
        "meta_data": [
            {"key": "stock_threshold", "value": "8"},
            {"key": "supplier", "value": {
                "name": "Apple Corporate Sales",
                "contact": "corporate@apple.com",
                "supplier_id": "SUP008"
            }}
        ]
    },
    {
        "id": 16,
        "name": "Microsoft Surface Pro 9",
        "sku": "MS-SURFACE-PRO9",
        "stock_quantity": 1,
        "meta_data": [
            {"key": "stock_threshold", "value": "6"},
            {"key": "supplier", "value": {
                "name": "Microsoft China",
                "contact": "surface@microsoft.cn",
                "supplier_id": "SUP007"
            }}
        ]
    },
    {
        "id": 15,
        "name": "Canon EOS R6",
        "sku": "CANON-EOS-R6",
        "stock_quantity": 4,
        "meta_data": [
            {"key": "stock_threshold", "value": "8"},
            {"key": "supplier", "value": {
                "name": "Canon Professional",
                "contact": "pro@canon.com.cn",
                "supplier_id": "SUP006"
            }}
        ]
    },
    {
        "id": 14,
        "name": "iPad Air 5th Gen",
        "sku": "IPAD-AIR-5",
        "stock_quantity": 7,
        "meta_data": [
            {"key": "stock_threshold", "value": "12"},
            {"key": "supplier", "value": {
                "name": "Apple Authorized Distributor",
                "contact": "sales@apple-distributor.cn",
                "supplier_id": "SUP002"
            }}
        ]
    },
    {
        "id": 12,
        "name": "Samsung 65 QLED TV",
        "sku": "SAMSUNG-Q80B-65",
        "stock_quantity": 2,
        "meta_data": [
            {"key": "stock_threshold", "value": "5"},
            {"key": "supplier", "value": {
                "name": "Samsung Display",
                "contact": "b2b@samsung.cn",
                "supplier_id": "SUP004"
            }}
        ]
    }
]

def get_meta_value(meta_data, key):
    for item in meta_data:
        if item.get("key") == key:
            return item.get("value")
    return None

def analyze_stock_levels(products):
    low_stock_products = []

    for product in products:
        stock_quantity = product.get("stock_quantity", 0)
        threshold_str = get_meta_value(product.get("meta_data", []), "stock_threshold")
        supplier_info = get_meta_value(product.get("meta_data", []), "supplier")

        try:
            threshold = int(threshold_str) if threshold_str else 0
        except (ValueError, TypeError):
            threshold = 0

        if stock_quantity < threshold:
            low_stock_product = {
                "product_id": product["id"],
                "product_name": product["name"],
                "sku": product["sku"],
                "current_stock": stock_quantity,
                "threshold": threshold,
                "supplier_name": supplier_info.get("name", "N/A") if supplier_info else "N/A",
                "supplier_contact": supplier_info.get("contact", "N/A") if supplier_info else "N/A",
                "status": "Low Stock",
                "date_added": datetime.now().strftime("%Y-%m-%d"),
                "last_updated": datetime.now().strftime("%Y-%m-%d %H:%M:%S")
            }
            low_stock_products.append(low_stock_product)

    return low_stock_products

# Analyze the products
low_stock_products = analyze_stock_levels(woocommerce_products)

print("=== STOCK ANALYSIS RESULTS ===")
print(f"Total products analyzed: {len(woocommerce_products)}")
print(f"Products with low stock: {len(low_stock_products)}")
print("\n=== LOW STOCK PRODUCTS ===")

for product in low_stock_products:
    print(f"• {product['product_name']} (SKU: {product['sku']})")
    print(f"  Current Stock: {product['current_stock']} | Threshold: {product['threshold']}")
    print(f"  Supplier: {product['supplier_name']} ({product['supplier_contact']})")
    print()

# Prepare data for Google Sheets update
sheets_data = []
for product in low_stock_products:
    sheets_data.append([
        str(product["product_id"]),
        product["product_name"],
        product["sku"],
        str(product["current_stock"]),
        str(product["threshold"]),
        product["supplier_name"],
        product["status"],
        product["supplier_contact"],
        product["date_added"],
        product["last_updated"]
    ])

print("=== GOOGLE SHEETS DATA PREPARED ===")
print(f"Rows to add to Google Sheets: {len(sheets_data)}")
for row in sheets_data:
    print(f"  {row}")

# Save data for use in other operations
with open('low_stock_data.json', 'w') as f:
    json.dump(low_stock_products, f, indent=2)

print("\n=== SUMMARY ===")
print(f"Found {len(low_stock_products)} products with stock below safety threshold")
print("Data saved to low_stock_data.json")

