 return data["products"]
// Calculate the final price after applying the discounts
def calculate_final_price(products):
 for product in products:
 price = product["price"]
 discount = product["discount"]
 final_price = price * (1 - discount)
 product["final_price"] = final_price
// Sort the products by final price in descending order
def sort_products_by_price(products):
 return sorted(products, key=lambda x: x["final_price"], reverse=True)
// Write the sorted products to a new JSON file
